import React from 'react';
import './HeartStatsCard.css';

const HeartStatsCard = ({ title, value, unit, icon, status = 'normal' }) => {
  const getStatusColor = (status) => {
    switch (status) {
      case 'good': return '#4caf50';
      case 'warning': return '#ff9800';
      case 'danger': return '#f44336';
      default: return '#e91e63';
    }
  };

  return (
    <div className={`heart-stats-card ${status}`}>
      <div className="card-header">
        <span className="card-icon" style={{ color: getStatusColor(status) }}>
          {icon}
        </span>
        <h3 className="card-title">{title}</h3>
      </div>
      <div className="card-content">
        <div className="metric-value">
          <span className="value">{value}</span>
          <span className="unit">{unit}</span>
        </div>
        <div className={`status-indicator status-${status}`}>
          {status === 'good' && '✓ Normal'}
          {status === 'warning' && '⚠ Check'}
          {status === 'danger' && '⚠ Alert'}
          {status === 'normal' && '○ Ready'}
        </div>
      </div>
    </div>
  );
};

export default HeartStatsCard;
